body.script:
const xmlData = `


            https://aminnepali.xyz/
            2024-09-09T12:28:41+00:00
            1.00


            https://aminnepali.xyz/project/project.html
            2024-09-09T12:28:41+00:00
            0.80


            https://aminnepali.xyz/project/study_files/index.html
            2024-09-09T12:28:41+00:00
            0.64


            https://aminnepali.xyz/project/mcq/multiple.html
            2024-09-09T12:28:41+00:00
            0.64


            https://aminnepali.xyz/project/calculator/calculator.html
            2024-09-09T12:28:41+00:00
            0.64


            https://aminnepali.xyz/project/student%20id/studentid.html
            2024-09-09T12:28:41+00:00
            0.64


            https://aminnepali.xyz/project/html_ide/htmlide.html
            2024-09-09T12:28:41+00:00
            0.64


            https://aminnepali.xyz/project/chat.html
            2024-09-09T12:28:41+00:00
            0.64


`;

const parser = new DOMParser();
const xmlDoc = parser.parseFromString(xmlData, "application/xml");
const urls = xmlDoc.getElementsByTagName("url");
const table = document.getElementById("sitemapTable");

for (let i = 0; i < urls.length; i++) {
    const row = table.insertRow();
    const locCell = row.insertCell(0);
    const lastmodCell = row.insertCell(1);
    const priorityCell = row.insertCell(2);

    locCell.innerText = urls[i].getElementsByTagName("loc")[0].textContent;
    lastmodCell.innerText = urls[i].getElementsByTagName("lastmod")[0].textContent;
    priorityCell.innerText = urls[i].getElementsByTagName("priority")[0].textContent;
}
